INSERT INTO `fa_admin_log` VALUES (35, 1, 'admin', '/admin8.php/goods/edit/ids/88?dialog=1', '藏品管理', '{\"dialog\":\"1\",\"row\":{\"name\":\"天宫小队\",\"goods_category_id\":\"27\",\"title\":\"随着地球文明对月球的探索分析不断深入，发现月球背面存在大量不为人知的外星文明活动的迹象\",\"image\":\"\\/uploads\\/20220818\\/14bb330ed6ae0a124a046a319bf1dab0.png\",\"images\":\"\\/uploads\\/20220818\\/14bb330ed6ae0a124a046a319bf1dab0.png\",\"price\":\"605.00\",\"type\":\"1\",\"fnstatus\":\"0\",\"fncount\":\"0\",\"goods_id\":\"\",\"content\":\"作品描述随着地球文明对月球的探索分析不断深入，发现月球背面存在大量不为人知的外星文明活动的迹象，于是虎王族的瑞安虎受命成立月球小队，探索月球背面一切可以利用的能源和科技并在月球背面建立基地。天宫小队也是在月球上抵御外星文明入侵地球的先遣队，为地球文明的发展，保驾护航。品牌描述美探世界是国内专业的数字艺术领域机构，专注于优质数字内容的创作生产。业务内容包含：元宇宙基建、IP运营、数字藏品制作发行、潮玩制作发行等。团队由阿里大麦网、腾讯微票创始团队、百度直播营销供应团队以及中国首个获得英国元宇宙红点设计奖项的团队、著名青年导演团队等共同组建。\",\"order\":\"0\",\"start_time\":\"2022-08-18 02:29:42\",\"end_time\":\"2026-10-27 02:29:42\",\"stock\":\"10000\",\"surplus\":\"10000\",\"company_name\":\"\",\"company_image\":\"\",\"creator\":\"诚正数藏\",\"owner\":\"诚正数藏\",\"casting_name\":\"诚正数藏\",\"casting_time\":\"2022-08-18 02:29:42\",\"is_show\":\"1\",\"label\":\"\",\"limit_number\":\"0\"},\"ids\":\"88\"}', '222.219.100.224', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/104.0.0.0 Safari/537.36', 1661946469);
