INSERT INTO `fa_admin_log` VALUES (79, 1, 'admin', '/admin8.php/goods/add?dialog=1', '藏品管理', '{\"dialog\":\"1\",\"row\":{\"is_manghe\":\"0\",\"is_can_buy\":\"1\",\"name\":\"金陵108景\",\"goods_category_id\":\"40\",\"title\":\"威武雄健的南朝石刻、桨声灯影的秦淮河、历经沧桑的明城墙、烟雨朦胧的鸡鸣寺……\",\"image\":\"\\/uploads\\/20220901\\/25bf9e28fc89245f0d780600c6fb7a63.png\",\"images\":\"\\/uploads\\/20220901\\/25bf9e28fc89245f0d780600c6fb7a63.png\",\"price\":\"560\",\"type\":\"1\",\"fnstatus\":\"0\",\"fncount\":\"\",\"goods_id\":\"\",\"hcstatus\":\"0\",\"hcgoods_id\":\"\",\"content\":\"画家卢山，从2016年开始，历时四年，画出金陵108景，把山水城林、历史文化、现代风貌都搬进水墨画里。以绿色为基调，象征沧桑起伏的南京，依然鲜活的生命与周全优美的风水；以时间为轴线，起自60万年前的“汤山猿人洞”，止于2018年之河西“双子塔”，致敬金陵的悠久历史与创新禀赋；以空间为铺展、以人物为灵魂、以建筑为载体，兼收城市与郊野、并蓄自然与人文，则是以笔墨描摹的“江南佳丽地，金陵帝王州”，以纸砚状写的金陵先祖、南京脸书……从水墨画中的《金陵108景》，感受南京饱经风霜、历史沧桑，却又有一种淡定、包容的状态，它也是博爱之都，充满了活力、生命力。卢山1979年6月生于江苏东台，现居南京；长期从事绘画探索及艺术规律的学术研究，试图构建一个思想与形式相融合的东方美学新秩序。曾毕业于苏州工艺美院、南京艺术学院、清华美院李铁生山水工作室硕士研究生班，同年任教于南京艺术学院。金陵108景独创人、独立艺术家，现为中央美院李铁生工作室研究员，南京艺术学院兼职教师、江苏省国画院特聘书法家、南京书画院特聘画家、独立艺术家。\",\"order\":\"\",\"start_time\":\"2022-09-01 01:45:16\",\"end_time\":\"2027-05-06 01:45:16\",\"stock\":\"10000\",\"company_name\":\"微意网络\",\"company_image\":\"\",\"creator\":\"微意网络\",\"owner\":\"微意网络\",\"casting_name\":\"微意网络\",\"casting_time\":\"2022-09-01 01:45:16\",\"is_show\":\"1\",\"label\":\"\",\"limit_number\":\"\"}}', '222.219.100.224', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/104.0.0.0 Safari/537.36', 1661967995);
